// src/components/LayoutCanvas.js

import React, { useRef, useEffect, useMemo, useState } from 'react';
import * as turf from '@turf/turf';

export default function LayoutCanvas({
  zoneW,
  zoneH,
  polygonPoints,
  closed,
  placements,
  setPlacements,
  margin // e.g. 0.207
}) {
  const canvasRef = useRef();
  const [dragging, setDragging] = useState(null);
  const scale = 100;    // px per meter
  const snapTol = 0.05; // meters

  // 1) Build the inward–buffered polygon (green boundary)
  const innerPoly = useMemo(() => {
    if (!closed || polygonPoints.length < 3) return null;
    const coords = polygonPoints.map(p => [p.x, p.y]);
    coords.push(coords[0]);
    const room = turf.polygon([coords]);
    const shrunk = turf.buffer(room, -margin, { units: 'meters' });
    return shrunk && shrunk.geometry ? shrunk : null;
  }, [polygonPoints, closed, margin]);

  // 2) Test if a tile at (x,y) with size w×h fits fully inside innerPoly
  function fitsInside(x, y, w, h) {
    if (!innerPoly) return false;
    const rect = turf.polygon([[
      [x,       y],
      [x + w,   y],
      [x + w,   y + h],
      [x,       y + h],
      [x,       y]
    ]]);
    return turf.booleanContains(innerPoly, rect);
  }

  // 3) Snap to other tile edges if within snapTol
  function applySnap(idx, x, y, w, h) {
    placements.forEach((t, j) => {
      if (j === idx) return;
      // vertical edges
      [[t.x], [t.x + t.width]].forEach(([sx]) => {
        if (Math.abs(x - sx) < snapTol) x = sx;
        if (Math.abs(x + w - sx) < snapTol) x = sx - w;
      });
      // horizontal edges
      [[t.y], [t.y + t.height]].forEach(([sy]) => {
        if (Math.abs(y - sy) < snapTol) y = sy;
        if (Math.abs(y + h - sy) < snapTol) y = sy - h;
      });
    });
    return { x, y };
  }

  // 4) Dragging logic
  useEffect(() => {
    const canvas = canvasRef.current;
    function toTileCoords(e) {
      const r = canvas.getBoundingClientRect();
      return {
        mx: (e.clientX - r.left)  / scale,
        my: (e.clientY - r.top)   / scale
      };
    }
    function onMouseDown(e) {
      const { mx, my } = toTileCoords(e);
      for (let i = placements.length - 1; i >= 0; i--) {
        const t = placements[i];
        if (mx >= t.x && mx <= t.x + t.width &&
            my >= t.y && my <= t.y + t.height) {
          setDragging({ idx: i, offsetX: mx - t.x, offsetY: my - t.y });
          return;
        }
      }
    }
    function onMouseMove(e) {
      if (!dragging) return;
      const { mx, my } = toTileCoords(e);
      const { idx, offsetX, offsetY } = dragging;
      const t = placements[idx];
      let nx = mx - offsetX;
      let ny = my - offsetY;
      ({ x: nx, y: ny } = applySnap(idx, nx, ny, t.width, t.height));
      if (fitsInside(nx, ny, t.width, t.height)) {
        const np = [...placements];
        np[idx] = { ...t, x: nx, y: ny };
        setPlacements(np);
      }
    }
    function onMouseUp() {
      if (dragging) setDragging(null);
    }
    canvas.addEventListener('mousedown', onMouseDown);
    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
    return () => {
      canvas.removeEventListener('mousedown', onMouseDown);
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };
  }, [dragging, placements, innerPoly]);

  // 5) Draw polygon, tiles, green boundary, and red boundary
  useEffect(() => {
    const c = canvasRef.current;
    const ctx = c.getContext('2d');
    c.width  = zoneW * scale;
    c.height = zoneH * scale;
    ctx.clearRect(0, 0, c.width, c.height);

    // Draw original polygon
    if (polygonPoints.length) {
      ctx.strokeStyle = '#0066CC'; ctx.lineWidth = 2;
      ctx.beginPath();
      polygonPoints.forEach((p, i) => {
        const X = p.x * scale, Y = p.y * scale;
        i === 0 ? ctx.moveTo(X, Y) : ctx.lineTo(X, Y);
      });
      if (closed) ctx.closePath();
      ctx.stroke();
      // vertices
      ctx.fillStyle = '#0066CC';
      polygonPoints.forEach(p => {
        ctx.beginPath();
        ctx.arc(p.x * scale, p.y * scale, 4, 0, 2 * Math.PI);
        ctx.fill();
      });
    }

    // Draw tiles
    placements.forEach(t => {
      const x = t.x * scale, y = t.y * scale;
      const w = t.width * scale, h = t.height * scale;
      ctx.fillStyle   = '#DDD';
      ctx.strokeStyle = '#333';
      ctx.lineWidth   = 1;
      ctx.fillRect(x, y, w, h);
      ctx.strokeRect(x, y, w, h);
    });

    if (!closed || placements.length === 0) return;

    // Draw green boundary: innerPoly
    if (innerPoly) {
      ctx.strokeStyle = 'green';
      ctx.setLineDash([4, 2]);
      ctx.lineWidth = 2;
      const g = innerPoly.geometry;
      const draw = ring => {
        ctx.beginPath();
        ring.forEach(([px, py], i) => {
          const X = px * scale, Y = py * scale;
          i === 0 ? ctx.moveTo(X, Y) : ctx.lineTo(X, Y);
        });
        ctx.closePath();
        ctx.stroke();
      };
      if (g.type === 'Polygon') {
        draw(g.coordinates[0]);
      } else {
        g.coordinates.forEach(poly => draw(poly[0]));
      }
      ctx.setLineDash([]);
    }

    // Build union of tiles and red boundary
    let union = null;
    placements.forEach(t => {
      const poly = turf.polygon([[
        [t.x, t.y],
        [t.x+t.width, t.y],
        [t.x+t.width, t.y+t.height],
        [t.x, t.y+t.height],
        [t.x, t.y]
      ]]);
      union = union ? turf.union(union, poly) : poly;
    });
    let redBuf = turf.buffer(union, margin, { units: 'meters' });
    // Clip redBuf so it doesn't cross inside green
    if (innerPoly) {
      const clipPoly = turf.polygon(innerPoly.geometry.coordinates);
      const inter = turf.intersect(redBuf, clipPoly);
      if (inter) redBuf = inter;
    }

    ctx.strokeStyle = 'red';
    ctx.setLineDash([6, 4]);
    ctx.lineWidth = 2;
    const rg = redBuf.geometry;
    const draw2 = ring => {
      ctx.beginPath();
      ring.forEach(([px, py], i) => {
        const X = px * scale, Y = py * scale;
        i === 0 ? ctx.moveTo(X, Y) : ctx.lineTo(X, Y);
      });
      ctx.closePath();
      ctx.stroke();
    };
    if (rg.type === 'Polygon') {
      draw2(rg.coordinates[0]);
    } else {
      rg.coordinates.forEach(poly => draw2(poly[0]));
    }
    ctx.setLineDash([]);

  }, [zoneW, zoneH, polygonPoints, closed, placements, innerPoly, margin]);

  return (
    <canvas
      ref={canvasRef}
      style={{
        border: '1px solid #333',
        cursor: dragging ? 'grabbing' : 'grab',
        marginTop: 20
      }}
    />
  );
}
