import React, { useRef, useEffect } from 'react';
import * as turf from '@turf/turf';

export default function LayoutCanvas({
  zoneW,
  zoneH,
  polygonPoints,
  isPolygonClosed,
  placements,
  margin  // in meters, e.g. 0.207
}) {
  const canvasRef = useRef(null);
  const scale = 100; // 100px = 1m

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx    = canvas.getContext('2d');
    canvas.width  = zoneW * scale;
    canvas.height = zoneH * scale;
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // --- Draw the original room polygon ---
    if (polygonPoints.length) {
      ctx.strokeStyle = '#0066CC';
      ctx.lineWidth = 2;
      ctx.beginPath();
      polygonPoints.forEach((pt, i) => {
        const X = pt.x * scale, Y = pt.y * scale;
        i === 0 ? ctx.moveTo(X, Y) : ctx.lineTo(X, Y);
      });
      if (isPolygonClosed) ctx.closePath();
      ctx.stroke();

      // draw vertices
      polygonPoints.forEach(pt => {
        ctx.fillStyle = '#CC0000';
        ctx.beginPath();
        ctx.arc(pt.x * scale, pt.y * scale, 4, 0, 2 * Math.PI);
        ctx.fill();
      });
    }

    // --- Draw placed tiles ---
    placements.forEach(t => {
      ctx.fillStyle   = '#DDDDDD';
      ctx.strokeStyle = '#333';
      ctx.lineWidth   = 1;
      ctx.fillRect(
        t.x * scale,
        t.y * scale,
        t.width * scale,
        t.height * scale
      );
      ctx.strokeRect(
        t.x * scale,
        t.y * scale,
        t.width * scale,
        t.height * scale
      );
    });

    // --- Draw 207 mm boundary around the UNION of tiles ---
    if (placements.length) {
      let unionPoly = null;
      placements.forEach(t => {
        const poly = turf.polygon([[
          [t.x,              t.y],
          [t.x + t.width,    t.y],
          [t.x + t.width,    t.y + t.height],
          [t.x,              t.y + t.height],
          [t.x,              t.y]
        ]]);
        unionPoly = unionPoly ? turf.union(unionPoly, poly) : poly;
      });

      const buffered = turf.buffer(unionPoly, margin, { units: 'meters' });
      if (buffered && buffered.geometry) {
        ctx.strokeStyle = '#FF0000';
        ctx.lineWidth   = 2;
        ctx.setLineDash([6, 4]);

        const drawRing = ring => {
          ctx.beginPath();
          ring.forEach(([px, py], i) => {
            const X = px * scale, Y = py * scale;
            i === 0 ? ctx.moveTo(X, Y) : ctx.lineTo(X, Y);
          });
          ctx.closePath();
          ctx.stroke();
        };

        const geom = buffered.geometry;
        if (geom.type === 'Polygon') {
          drawRing(geom.coordinates[0]);
        } else { // MultiPolygon
          geom.coordinates.forEach(polyCoords => drawRing(polyCoords[0]));
        }

        ctx.setLineDash([]);
      }
    }
  }, [zoneW, zoneH, polygonPoints, isPolygonClosed, placements, margin]);

  return (
    <canvas
      ref={canvasRef}
      style={{ border: '1px solid #333', marginTop: 20 }}
    />
  );
}
