import React, { useState } from 'react';
import * as turf from '@turf/turf';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';
import './index.css';

export default function App() {
  // Canvas size
  const [zoneW, setZoneW] = useState(10);
  const [zoneH, setZoneH] = useState(6);

  // Polygon definition
  const [polygonPoints, setPolygonPoints] = useState([]);
  const [closed, setClosed] = useState(false);
  const [sideLen, setSideLen] = useState('');
  const [sideAng, setSideAng] = useState('');

  // Layout options
  const [startCorner, setStartCorner] = useState('bottom-left');
  const [orientation, setOrientation] = useState('both');
  const [tileW, setTileW] = useState(1.26);
  const [margin, setMargin] = useState(0.207);
  const lengths = [4, 2, 1];

  // Pricing
  const [prices, setPrices] = useState({4:20,2:12,1:6});

  // Placements & counts
  const [placements, setPlacements] = useState([]);
  const [counts, setCounts] = useState({4:0,2:0,1:0});

  // Manual add/delete tiles
  const [newTileLen, setNewTileLen] = useState(4);

  // Boundary & border pricing
  const [pricePerMeter, setPricePerMeter] = useState(0);
  const [boundaryPerimeter, setBoundaryPerimeter] = useState(0);
  const [boundaryCost, setBoundaryCost] = useState(0);

  // Add one polygon side
  const addSide = () => {
    const L = parseFloat(sideLen), A = parseFloat(sideAng) * Math.PI/180;
    if (isNaN(L) || isNaN(A)) return;
    const last = polygonPoints.length
      ? polygonPoints[polygonPoints.length - 1]
      : { x: 0, y: 0 };
    setPolygonPoints([
      ...polygonPoints,
      { x: last.x + L * Math.cos(A), y: last.y + L * Math.sin(A) }
    ]);
    setSideLen(''); setSideAng('');
  };

  // Close polygon
  const finishPoly = () => {
    if (polygonPoints.length >= 3) setClosed(true);
  };

  // Reset all
  const resetAll = () => {
    setPolygonPoints([]); setClosed(false);
    setPlacements([]); setCounts({4:0,2:0,1:0});
    setBoundaryPerimeter(0); setBoundaryCost(0);
  };

  // Optimize initial layout
  const optimize = () => {
    const { placements: pl, counts: cs } = optimizeLayout(
      polygonPoints, lengths, tileW, margin,
      orientation, startCorner
    );
    setPlacements(pl);
    setCounts(cs);
    setBoundaryPerimeter(0);
    setBoundaryCost(0);
  };

  // Add a single tile at top‑left of the inner polygon
  const addTile = () => {
    if (!closed) return;
    const room = turf.polygon([[
      ...polygonPoints.map(p => [p.x, p.y]),
      [polygonPoints[0].x, polygonPoints[0].y]
    ]]);
    const inner = turf.buffer(room, -margin, { units: 'meters' });
    const [minX, minY] = turf.bbox(inner);
    setPlacements(ps => [
      ...ps,
      { x: minX, y: minY, width: newTileLen, height: tileW, length: newTileLen }
    ]);
  };

  // Remove tile by index
  const removeTile = idx => {
    setPlacements(ps => ps.filter((_,i) => i !== idx));
  };

  // Calculate the red‑line boundary & cost
  const calculateBoundary = () => {
    if (placements.length === 0) {
      setBoundaryPerimeter(0);
      setBoundaryCost(0);
      return;
    }

    // 1) Union all tile rectangles
    let unionPoly = null;
    placements.forEach(t => {
      const poly = turf.polygon([[
        [t.x,            t.y],
        [t.x + t.width,  t.y],
        [t.x + t.width,  t.y + t.height],
        [t.x,            t.y + t.height],
        [t.x,            t.y]
      ]]);
      unionPoly = unionPoly ? turf.union(unionPoly, poly) : poly;
    });

    // 2) Extract the outer linestring(s)
    const line = turf.polygonToLine(unionPoly);

    // 3) Measure length
    let peri = 0;
    const geom = line.geometry;
    const segments = geom.type === 'LineString'
      ? [geom.coordinates]
      : geom.coordinates;
    segments.forEach(coords => {
      for (let i = 0; i < coords.length - 1; i++) {
        const [x1,y1] = coords[i], [x2,y2] = coords[i+1];
        peri += Math.hypot(x2 - x1, y2 - y1);
      }
    });

    setBoundaryPerimeter(peri);
    setBoundaryCost(peri * pricePerMeter);
  };

  // Compute tile cost
  const tileCost = placements.reduce(
    (sum, t) => sum + (prices[t.length] || 0),
    0
  );

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      {/* Canvas & polygon controls */}
      <div className="controls">
        <label>
          Canvas width (m):
          <input
            type="number"
            value={zoneW}
            onChange={e => setZoneW(+e.target.value || 0)}
          />
        </label>
        <label>
          Canvas height (m):
          <input
            type="number"
            value={zoneH}
            onChange={e => setZoneH(+e.target.value || 0)}
          />
        </label>
        <button onClick={resetAll}>Reset All</button>
      </div>

      <div className="controls">
        <label>
          Side length (m):
          <input
            type="number" step="0.01"
            value={sideLen}
            onChange={e => setSideLen(e.target.value)}
            disabled={closed}
          />
        </label>
        <label>
          Angle (°):
          <input
            type="number" step="1"
            value={sideAng}
            onChange={e => setSideAng(e.target.value)}
            disabled={closed}
          />
        </label>
        <button onClick={addSide} disabled={closed}>Add Side</button>
        <button
          onClick={finishPoly}
          disabled={closed || polygonPoints.length < 3}
        >
          Close Polygon
        </button>
      </div>

      {/* Layout & pricing options */}
      <div className="controls">
        <label>
          Start corner:
          <select
            value={startCorner}
            onChange={e => setStartCorner(e.target.value)}
            disabled={!closed}
          >
            <option value="bottom-left">Bottom‑left</option>
            <option value="bottom-right">Bottom‑right</option>
            <option value="top-right">Top‑right</option>
            <option value="top-left">Top‑left</option>
          </select>
        </label>
        <label>
          Orientation:
          <select
            value={orientation}
            onChange={e => setOrientation(e.target.value)}
            disabled={!closed}
          >
            <option value="both">Both</option>
            <option value="horizontal">Horizontal only</option>
            <option value="vertical">Vertical only</option>
          </select>
        </label>
        <label>
          Tile short side (m):
          <input
            type="number" step="0.01"
            value={tileW}
            onChange={e => setTileW(+e.target.value || 0)}
            disabled={!closed}
          />
        </label>
        <label>
          Margin (m):
          <input
            type="number" step="0.001"
            value={margin}
            onChange={e => setMargin(+e.target.value || 0)}
            disabled={!closed}
          />
        </label>
        <button onClick={optimize} disabled={!closed}>
          Optimize Layout
        </button>
      </div>

      {/* Add/Delete tiles */}
      {closed && (
        <div className="controls">
          <label>
            New tile length:
            <select
              value={newTileLen}
              onChange={e => setNewTileLen(+e.target.value)}
            >
              {lengths.map(L => (
                <option key={L} value={L}>{L} m</option>
              ))}
            </select>
          </label>
          <button onClick={addTile}>Add Tile</button>
          <span style={{ marginLeft: '1em' }}>(Double‑click a tile to delete)</span>
        </div>
      )}

      {/* Costs display */}
      {placements.length > 0 && (
        <div className="controls">
          <p>Total tiles: {placements.length}</p>
          <p>Tile cost: {tileCost.toFixed(2)} lei</p>
        </div>
      )}

      {/* Boundary & price */}
      {placements.length > 0 && (
        <div className="controls">
          <label>
            Price per meter (border):
            <input
              type="number" step="0.01"
              value={pricePerMeter}
              onChange={e => setPricePerMeter(+e.target.value || 0)}
            />
          </label>
          <button onClick={calculateBoundary}>Calculate Boundary & Price</button>
          <p>Boundary length: {boundaryPerimeter.toFixed(2)} m</p>
          <p>Boundary cost: {boundaryCost.toFixed(2)} lei</p>
        </div>
      )}

      {/* The draggable/snap/delete canvas */}
      <LayoutCanvas
        zoneW={zoneW}
        zoneH={zoneH}
        polygonPoints={polygonPoints}
        closed={closed}
        placements={placements}
        setPlacements={setPlacements}
        removeTile={removeTile}
        margin={margin}
      />
    </div>
  );
}
