// Greedy, edge-to-edge packer with orientation modes
export default function optimizeLayout(polygonPoints, zoneW, zoneH, lengths,
// [4,2,1]
tileW,
// 1.26
margin,
// 0.207
orientation // 'both' | 'horizontal' | 'vertical'
) {
  // point-in-polygon (ray‑cast)
  function pointInPoly(x, y, vs) {
    let inside = false;
    for (let i = 0, j = vs.length - 1; i < vs.length; j = i++) {
      const xi = vs[i].x,
        yi = vs[i].y;
      const xj = vs[j].x,
        yj = vs[j].y;
      const intersect = yi > y !== yj > y && x < (xj - xi) * (y - yi) / (yj - yi) + xi;
      if (intersect) inside = !inside;
    }
    return inside;
  }

  // distance point→segment
  function ptSegDist(px, py, x1, y1, x2, y2) {
    const A = px - x1,
      B = py - y1;
    const C = x2 - x1,
      D = y2 - y1;
    const dot = A * C + B * D;
    const len2 = C * C + D * D;
    let t = len2 !== 0 ? dot / len2 : -1;
    let xx, yy;
    if (t < 0) {
      xx = x1;
      yy = y1;
    } else if (t > 1) {
      xx = x2;
      yy = y2;
    } else {
      xx = x1 + t * C;
      yy = y1 + t * D;
    }
    const dx = px - xx,
      dy = py - yy;
    return Math.sqrt(dx * dx + dy * dy);
  }
  const placements = [];
  const counts = {
    4: 0,
    2: 0,
    1: 0
  };
  function isValid(x, y, w, h) {
    // define the four corners
    const corners = [{
      x: x,
      y: y
    }, {
      x: x + w,
      y: y
    }, {
      x: x,
      y: y + h
    }, {
      x: x + w,
      y: y + h
    }];

    // 1) each corner inside room polygon
    for (const c of corners) {
      if (!pointInPoly(c.x, c.y, polygonPoints)) return false;
    }

    // 2) each corner at least 'margin' from every polygon edge
    for (const c of corners) {
      for (let i = 0, j = polygonPoints.length - 1; i < polygonPoints.length; j = i++) {
        const p1 = polygonPoints[i];
        const p2 = polygonPoints[j];
        if (ptSegDist(c.x, c.y, p1.x, p1.y, p2.x, p2.y) < margin) {
          return false;
        }
      }
    }

    // 3) no overlap with existing tiles
    for (const t of placements) {
      if (!(x + w <= t.x || t.x + t.width <= x || y + h <= t.y || t.y + t.height <= y)) {
        return false;
      }
    }
    return true;
  }

  // Fill rows (horizontal-long tiles)
  function doHorizontal() {
    const startY = margin;
    const rows = Math.floor((zoneH - 2 * margin) / tileW);
    for (let r = 0; r < rows; r++) {
      const y = startY + r * tileW;
      let x = margin;
      while (x < zoneW - margin - 1e-6) {
        let placed = false;
        for (const L of lengths) {
          if (x + L <= zoneW - margin + 1e-6 && isValid(x, y, L, tileW)) {
            placements.push({
              x,
              y,
              width: L,
              height: tileW,
              length: L
            });
            counts[L]++;
            x += L;
            placed = true;
            break;
          }
        }
        if (!placed) break;
      }
    }
  }

  // Fill columns (vertical-long tiles)
  function doVertical() {
    const startX = margin;
    const cols = Math.floor((zoneW - 2 * margin) / tileW);
    for (let c = 0; c < cols; c++) {
      const x = startX + c * tileW;
      let y = margin;
      while (y < zoneH - margin - 1e-6) {
        let placed = false;
        for (const L of lengths) {
          if (y + L <= zoneH - margin + 1e-6 && isValid(x, y, tileW, L)) {
            placements.push({
              x,
              y,
              width: tileW,
              height: L,
              length: L
            });
            counts[L]++;
            y += L;
            placed = true;
            break;
          }
        }
        if (!placed) break;
      }
    }
  }

  // Choose orientation mode
  if (orientation === 'both' || orientation === 'horizontal') {
    doHorizontal();
  }
  if (orientation === 'both' || orientation === 'vertical') {
    doVertical();
  }
  return {
    placements,
    counts
  };
}

