import React, { useState } from 'react';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';
import './index.css';

function App() {
  const [polygonPoints, setPolygonPoints] = useState([]);
  const [closed, setClosed] = useState(false);
  const [sideLen, setSideLen] = useState('');
  const [sideAng, setSideAng] = useState('');
  const [startCorner, setStartCorner] = useState('bottom-left');
  const [orientation, setOrientation] = useState('both');
  const [placements, setPlacements] = useState([]);
  const [counts, setCounts] = useState({4:0,2:0,1:0});
  const [prices, setPrices] = useState({4:20,2:12,1:6});

  // add side by length+angle
  const addSide = () => {
    const L = parseFloat(sideLen), A = parseFloat(sideAng)*Math.PI/180;
    if (isNaN(L)||isNaN(A)) return;
    const last = polygonPoints.length
      ? polygonPoints[polygonPoints.length-1]
      : {x:0,y:0};
    setPolygonPoints([
      ...polygonPoints,
      { x: last.x + L*Math.cos(A), y: last.y + L*Math.sin(A) }
    ]);
    setSideLen(''); setSideAng('');
  };

  const finishPoly = () => {
    if (polygonPoints.length >= 3) setClosed(true);
  };
  const reset = () => {
    setPolygonPoints([]); setClosed(false);
    setPlacements([]); setCounts({4:0,2:0,1:0});
  };

  const optimize = () => {
    const M = 0.207, W = 1.26;          // margin, tile short side
    const lengths = [4,2,1];
    const { placements: pls, counts: cs } = optimizeLayout(
      polygonPoints, lengths, W, M,
      orientation, startCorner
    );
    setPlacements(pls);
    setCounts(cs);
  };

  const totalCost = 
    counts[4]*prices[4] +
    counts[2]*prices[2] +
    counts[1]*prices[1];

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      <div className="controls">
        <button onClick={reset}>Reset</button>
      </div>

      {!closed && <>
        <div className="controls">
          <label>
            Lungime latură (m):
            <input
              type="number" step="0.01"
              value={sideLen}
              onChange={e=>setSideLen(e.target.value)}
            />
          </label>
          <label>
            Unghi (°):
            <input
              type="number" step="1"
              value={sideAng}
              onChange={e=>setSideAng(e.target.value)}
            />
          </label>
          <button onClick={addSide}>Adaugă Latură</button>
          {polygonPoints.length>=3 && (
            <button onClick={finishPoly}>Închide Poligon</button>
          )}
        </div>
        {polygonPoints.length>0 && (
          <div className="controls">
            <strong>Laturi:</strong>{' '}
            {polygonPoints.map((p,i)=>(
              <span key={i}>{i+1}→{i+2}: </span>
            ))}
          </div>
        )}
      </>}

      {closed && (
        <div className="controls">
          <label>
            Corner:
            <select
              value={startCorner}
              onChange={e=>setStartCorner(e.target.value)}
            >
              <option value="bottom-left">Bottom‑left</option>
              <option value="bottom-right">Bottom‑right</option>
              <option value="top-right">Top‑right</option>
              <option value="top-left">Top‑left</option>
            </select>
          </label>
          <label>
            Orientare:
            <select
              value={orientation}
              onChange={e=>setOrientation(e.target.value)}
            >
              <option value="both">Both</option>
              <option value="horizontal">Horizontal</option>
              <option value="vertical">Vertical</option>
            </select>
          </label>
          <button onClick={optimize}>Optimizează</button>
        </div>
      )}

      <LayoutCanvas
        polygonPoints={polygonPoints}
        closed={closed}
        placements={placements}
        margin={0.207}
      />

      {placements.length>0 && (
        <div className="controls">
          <label>
            Preț 4 m:
            <input
              type="number" step="0.01"
              value={prices[4]}
              onChange={e=>setPrices({...prices,4:+e.target.value})}
            />
          </label>
          <label>
            Preț 2 m:
            <input
              type="number" step="0.01"
              value={prices[2]}
              onChange={e=>setPrices({...prices,2:+e.target.value})}
            />
          </label>
          <label>
            Preț 1 m:
            <input
              type="number" step="0.01"
              value={prices[1]}
              onChange={e=>setPrices({...prices,1:+e.target.value})}
            />
          </label>
          <p>Cost total: {totalCost.toFixed(2)} lei</p>
        </div>
      )}
    </div>
  );
}

export default App;
