import React, { useRef, useEffect } from 'react';

export default function LayoutCanvas({
  zoneW,
  zoneH,
  polygonPoints,
  isPolygonClosed,
  placements,
  margin,
  onCanvasClick
}) {
  const canvasRef = useRef(null);
  const scale = 100; // 100 px per meter

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = zoneW * scale;
    canvas.height = zoneH * scale;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // draw user polygon
    if (polygonPoints.length > 0) {
      ctx.strokeStyle = '#0066CC';
      ctx.lineWidth = 2;
      ctx.beginPath();
      polygonPoints.forEach((pt, i) => {
        const x = pt.x * scale;
        const y = pt.y * scale;
        i === 0 ? ctx.moveTo(x, y) : ctx.lineTo(x, y);
      });
      if (isPolygonClosed) ctx.closePath();
      ctx.stroke();

      // draw vertices
      polygonPoints.forEach(pt => {
        ctx.fillStyle = '#CC0000';
        ctx.beginPath();
        ctx.arc(pt.x * scale, pt.y * scale, 4, 0, Math.PI * 2);
        ctx.fill();
      });
    }

    // draw placed tiles
    placements.forEach(tile => {
      ctx.fillStyle = '#DDDDDD';
      ctx.fillRect(
        tile.x * scale,
        tile.y * scale,
        tile.width * scale,
        tile.height * scale
      );
      ctx.strokeStyle = '#000000';
      ctx.strokeRect(
        tile.x * scale,
        tile.y * scale,
        tile.width * scale,
        tile.height * scale
      );
    });
  }, [zoneW, zoneH, polygonPoints, isPolygonClosed, placements]);

  const handleClick = e => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = (e.clientX - rect.left) / scale;
    const y = (e.clientY - rect.top) / scale;
    onCanvasClick(x, y);
  };

  return (
    <canvas
      ref={canvasRef}
      onClick={handleClick}
      style={{ border: '1px solid #333', marginTop: 20 }}
    />
  );
}
