import React, { useState } from 'react';
import * as turf from '@turf/turf';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';
import './index.css';

export default function App() {
  // Canvas size
  const [zoneW, setZoneW] = useState(10);
  const [zoneH, setZoneH] = useState(6);

  // Polygon definition
  const [polygonPoints, setPolygonPoints] = useState([]);
  const [closed, setClosed]             = useState(false);
  const [sideLen, setSideLen]           = useState('');
  const [sideAng, setSideAng]           = useState('');

  // Layout options
  const lengths       = [4,2,1];
  const [startCorner, setStartCorner] = useState('bottom-left');
  const [orientation, setOrientation] = useState('both');
  const [tileW, setTileW]             = useState(1.26);
  const [margin, setMargin]           = useState(0.207);

  // Pricing
  const [prices, setPrices] = useState({4:20,2:12,1:6});

  // Placements & counts
  const [placements, setPlacements] = useState([]);
  const [counts, setCounts]         = useState({4:0,2:0,1:0});

  // Manual add/delete
  const [newTileLen, setNewTileLen] = useState(4);

  // Boundary & cost
  const [pricePerMeter, setPricePerMeter]     = useState(0);
  const [boundaryPerimeter, setBoundaryPerimeter] = useState(0);
  const [boundaryCost, setBoundaryCost]       = useState(0);

  // Add a polygon side
  const addSide = () => {
    const L = parseFloat(sideLen),
          A = parseFloat(sideAng) * Math.PI/180;
    if (!L||!A) return;
    const last = polygonPoints.length
      ? polygonPoints[polygonPoints.length-1]
      : {x:0,y:0};
    setPolygonPoints([
      ...polygonPoints,
      { x: last.x + L*Math.cos(A), y: last.y + L*Math.sin(A) }
    ]);
    setSideLen(''); setSideAng('');
  };

  const finishPoly = () => {
    if (polygonPoints.length>=3) setClosed(true);
  };

  const resetAll = () => {
    setPolygonPoints([]); setClosed(false);
    setPlacements([]); setCounts({4:0,2:0,1:0});
    setBoundaryPerimeter(0); setBoundaryCost(0);
  };

  // Optimize layout
  const optimize = () => {
    const { placements: pl, counts: cs } = optimizeLayout(
      polygonPoints, lengths, tileW, margin,
      orientation, startCorner
    );
    setPlacements(pl);
    setCounts(cs);
    setBoundaryPerimeter(0);
    setBoundaryCost(0);
  };

  // Add one tile
  const addTile = () => {
    if (!closed) return;
    const room  = turf.polygon([[
      ...polygonPoints.map(p=>[p.x,p.y]),
      [polygonPoints[0].x,polygonPoints[0].y]
    ]]);
    const inner = turf.buffer(room, -margin, { units:'meters' });
    const [minX,minY] = turf.bbox(inner);
    setPlacements(ps=>[
      ...ps,
      { x: minX, y: minY, width: newTileLen, height: tileW, length: newTileLen }
    ]);
  };

  // Delete by index
  const removeTile = idx => {
    setPlacements(ps=>ps.filter((_,i)=>i!==idx));
  };

  // ==== UPDATED calculateBoundary: true outward offset perimeter ====
  const calculateBoundary = () => {
    if (placements.length===0) {
      setBoundaryPerimeter(0);
      setBoundaryCost(0);
      return;
    }

    // 1) Union all your tile rectangles
    let unionPoly = null;
    placements.forEach(t => {
      const poly = turf.polygon([[
        [t.x,            t.y],
        [t.x + t.width,  t.y],
        [t.x + t.width,  t.y + t.height],
        [t.x,            t.y + t.height],
        [t.x,            t.y]
      ]]);
      unionPoly = unionPoly ? turf.union(unionPoly, poly) : poly;
    });

    // 2) Microâ€‘buffer to fuse any tiny gaps
    unionPoly = turf.buffer(unionPoly, 1e-6, { units:'meters' });
    unionPoly = turf.buffer(unionPoly, -1e-6, { units:'meters' });

    // 3) **Buffer outward** by your `margin` (configurable)
    const offsetPoly = turf.buffer(unionPoly, margin, {
      units:'meters',
      steps:1        // square corners so length = raw + 8*margin, etc.
    });

    // 4) Convert to line(s) and **measure** its length
    const outline = turf.polygonToLine(offsetPoly);
    const lenKm   = turf.length(outline, { units:'kilometers' });
    const peri    = lenKm * 1000; // to meters

    setBoundaryPerimeter(peri);
    setBoundaryCost(peri * pricePerMeter);
  };

  // Tile cost
  const tileCost = placements.reduce(
    (sum,t) => sum + (prices[t.length]||0),
    0
  );

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      {/* Canvas & polygon */}
      <div className="controls">
        <label>
          Width (m):
          <input type="number" value={zoneW}
                 onChange={e=>setZoneW(+e.target.value||0)} />
        </label>
        <label>
          Height (m):
          <input type="number" value={zoneH}
                 onChange={e=>setZoneH(+e.target.value||0)} />
        </label>
        <button onClick={resetAll}>Reset All</button>
      </div>

      <div className="controls">
        <label>
          Side len (m):
          <input type="number" step="0.01" value={sideLen}
                 onChange={e=>setSideLen(e.target.value)} disabled={closed}/>
        </label>
        <label>
          Angle (Â°):
          <input type="number" step="1" value={sideAng}
                 onChange={e=>setSideAng(e.target.value)} disabled={closed}/>
        </label>
        <button onClick={addSide} disabled={closed}>Add Side</button>
        <button onClick={finishPoly}
                disabled={closed||polygonPoints.length<3}>
          Close Polygon
        </button>
      </div>

      {/* Layout settings */}
      <div className="controls">
        <label>
          Start corner:
          <select value={startCorner}
                  onChange={e=>setStartCorner(e.target.value)}
                  disabled={!closed}>
            <option value="bottom-left">BL</option>
            <option value="bottom-right">BR</option>
            <option value="top-right">TR</option>
            <option value="top-left">TL</option>
          </select>
        </label>
        <label>
          Orientation:
          <select value={orientation}
                  onChange={e=>setOrientation(e.target.value)}
                  disabled={!closed}>
            <option value="both">Both</option>
            <option value="horizontal">H only</option>
            <option value="vertical">V only</option>
          </select>
        </label>
        <label>
          Tile short side (m):
          <input type="number" step="0.01" value={tileW}
                 onChange={e=>setTileW(+e.target.value||0)}
                 disabled={!closed}/>
        </label>
        <label>
          Offset (m):
          <input type="number" step="0.001" value={margin}
                 onChange={e=>setMargin(+e.target.value||0)}
                 disabled={!closed}/>
        </label>
        <button onClick={optimize} disabled={!closed}>Optimize</button>
      </div>

      {/* Add/Delete tiles */}
      {closed && (
        <div className="controls">
          <label>
            New tile len:
            <select value={newTileLen}
                    onChange={e=>setNewTileLen(+e.target.value)}>
              {lengths.map(L=>(
                <option key={L} value={L}>{L}Â m</option>
              ))}
            </select>
          </label>
          <button onClick={addTile}>Add Tile</button>
          <span style={{marginLeft:12}}>(dblâ€‘click to delete)</span>
        </div>
      )}

      {/* Costs */}
      {placements.length>0 && (
        <div className="controls">
          <p>Tiles: {placements.length}</p>
          <p>Tile cost: {tileCost.toFixed(2)}Â lei</p>
        </div>
      )}

      {/* Boundary & price */}
      {placements.length>0 && (
        <div className="controls">
          <label>
            Price per meter:
            <input type="number" step="0.01"
                   value={pricePerMeter}
                   onChange={e=>setPricePerMeter(+e.target.value||0)} />
          </label>
          <button onClick={calculateBoundary}>
            Calculate Boundary
          </button>
          <p>Offset perimeter: {boundaryPerimeter.toFixed(2)}Â m</p>
          <p>Boundary cost: {boundaryCost.toFixed(2)}Â lei</p>
        </div>
      )}

      {/* Canvas */}
      <LayoutCanvas
        zoneW={zoneW}
        zoneH={zoneH}
        polygonPoints={polygonPoints}
        closed={closed}
        placements={placements}
        setPlacements={setPlacements}
        removeTile={removeTile}
        margin={margin}
      />
    </div>
  );
}
