import React, { useState } from 'react';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';

function App() {
  const [zoneW, setZoneW] = useState(10);
  const [zoneH, setZoneH] = useState(6);

  const [polygonPoints, setPolygonPoints] = useState([]);
  const [isPolygonClosed, setIsPolygonClosed] = useState(false);
  const [sideLength, setSideLength] = useState('');
  const [sideAngle, setSideAngle] = useState('');

  const [orientation, setOrientation] = useState('both');
  const [startCorner, setStartCorner] = useState('bottom-left');

  const [placements, setPlacements] = useState([]);
  const [totalCost, setTotalCost] = useState(0);
  const [price4, setPrice4] = useState(20);
  const [price2, setPrice2] = useState(12);
  const [price1, setPrice1] = useState(6);

  // Build polygon side by side+angle
  const handleAddSide = () => {
    const L = parseFloat(sideLength);
    const A = parseFloat(sideAngle) * Math.PI / 180;
    if (isNaN(L) || isNaN(A)) return;
    const last = polygonPoints.length
      ? polygonPoints[polygonPoints.length - 1]
      : { x: 0, y: 0 };
    const next = {
      x: last.x + L * Math.cos(A),
      y: last.y + L * Math.sin(A),
    };
    setPolygonPoints([...polygonPoints, next]);
    setSideLength('');
    setSideAngle('');
  };

  const handleFinishPolygon = () => {
    if (polygonPoints.length >= 3) {
      setIsPolygonClosed(true);
    }
  };

  const handleReset = () => {
    setPolygonPoints([]);
    setIsPolygonClosed(false);
    setPlacements([]);
    setTotalCost(0);
  };

  const handleOptimize = () => {
    const margin = 0.207;
    const tileW = 1.26;
    const lengths = [4, 2, 1];

    const { placements: pl, counts } = optimizeLayout(
      polygonPoints,
      zoneW,
      zoneH,
      lengths,
      tileW,
      margin,
      orientation,
      startCorner
    );
    setPlacements(pl);
    setTotalCost(counts[4]*price4 + counts[2]*price2 + counts[1]*price1);
  };

  const sideLengths = polygonPoints.map((pt, i) => {
    const next = polygonPoints[(i+1)%polygonPoints.length] || pt;
    return Math.hypot(next.x-pt.x, next.y-pt.y).toFixed(2);
  });

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      {/* Room bbox and reset */}
      <div className="controls">
        <label>
          Zona lățime (m):
          <input
            type="number"
            value={zoneW}
            onChange={e=>setZoneW(parseFloat(e.target.value)||0)}
          />
        </label>
        <label>
          Zona înălțime (m):
          <input
            type="number"
            value={zoneH}
            onChange={e=>setZoneH(parseFloat(e.target.value)||0)}
          />
        </label>
        <button onClick={handleReset}>Reset Room</button>
      </div>

      {/* Define polygon by sides */}
      <div className="controls">
        {!isPolygonClosed && (
          <>
            <label>
              Lungime latură (m):
              <input
                type="number"
                step="0.01"
                value={sideLength}
                onChange={e=>setSideLength(e.target.value)}
              />
            </label>
            <label>
              Unghi (° de la orizontală):
              <input
                type="number"
                step="1"
                value={sideAngle}
                onChange={e=>setSideAngle(e.target.value)}
              />
            </label>
            <button onClick={handleAddSide}>Adaugă Latură</button>
            {polygonPoints.length>=3 && (
              <button onClick={handleFinishPolygon}>Închide Poligon</button>
            )}
          </>
        )}
      </div>

      {/* Show side lengths */}
      {polygonPoints.length>0 && (
        <div className="controls">
          <strong>Laturi definite:</strong>
          <ul>
            {sideLengths.map((len,i)=>(
              <li key={i}>Latura {i+1}: {len} m</li>
            ))}
          </ul>
        </div>
      )}

      {/* Options: start corner & orientation */}
      {isPolygonClosed && (
        <div className="controls">
          <label>
            Start corner:
            <select
              value={startCorner}
              onChange={e=>setStartCorner(e.target.value)}
            >
              <option value="bottom-left">Bottom‑left</option>
              <option value="bottom-right">Bottom‑right</option>
              <option value="top-right">Top‑right</option>
              <option value="top-left">Top‑left</option>
            </select>
          </label>
          <label>
            Orientare:
            <select
              value={orientation}
              onChange={e=>setOrientation(e.target.value)}
            >
              <option value="both">Both</option>
              <option value="horizontal">Horizontal only</option>
              <option value="vertical">Vertical only</option>
            </select>
          </label>
        </div>
      )}

      {/* Canvas */}
      <LayoutCanvas
        zoneW={zoneW}
        zoneH={zoneH}
        polygonPoints={polygonPoints}
        isPolygonClosed={isPolygonClosed}
        placements={placements}
        margin={0.207}
      />

      {/* Pricing & optimize */}
      {isPolygonClosed && (
        <div className="controls">
          <label>
            Preț 4 m:
            <input
              type="number"
              step="0.01"
              value={price4}
              onChange={e=>setPrice4(parseFloat(e.target.value)||0)}
            />
          </label>
          <label>
            Preț 2 m:
            <input
              type="number"
              step="0.01"
              value={price2}
              onChange={e=>setPrice2(parseFloat(e.target.value)||0)}
            />
          </label>
          <label>
            Preț 1 m:
            <input
              type="number"
              step="0.01"
              value={price1}
              onChange={e=>setPrice1(parseFloat(e.target.value)||0)}
            />
          </label>
          <button onClick={handleOptimize}>Optimizează Așezarea</button>
        </div>
      )}

      {/* Results */}
      {placements.length>0 && (
        <div className="result">
          <p>Plăci 4 m: {placements.filter(p=>p.length===4).length}</p>
          <p>Plăci 2 m: {placements.filter(p=>p.length===2).length}</p>
          <p>Plăci 1 m: {placements.filter(p=>p.length===1).length}</p>
          <p>Cost total: {totalCost.toFixed(2)} lei</p>
        </div>
      )}
    </div>
  );
}

export default App;
