import React, { useRef, useEffect } from 'react';

export default function LayoutCanvas({ placements, zoneW, zoneH, margin }) {
  const canvasRef = useRef(null);
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const scale = 50; // 50px per meter
    const widthPx = zoneW * scale;
    const heightPx = zoneH * scale;
    canvas.width = widthPx;
    canvas.height = heightPx;
    ctx.clearRect(0, 0, widthPx, heightPx);

    // draw margin
    ctx.strokeStyle = '#f00';
    ctx.lineWidth = 2;
    ctx.strokeRect(
      margin * scale,
      margin * scale,
      widthPx - 2 * margin * scale,
      heightPx - 2 * margin * scale
    );

    // draw tiles
    placements.forEach(p => {
      ctx.fillStyle = '#ddd';
      ctx.fillRect(p.x * scale, p.y * scale, p.size * scale, p.size * scale);
      ctx.strokeStyle = '#000';
      ctx.strokeRect(p.x * scale, p.y * scale, p.size * scale, p.size * scale);
    });
  }, [placements, zoneW, zoneH, margin]);

  return <canvas ref={canvasRef} style={{ border: '1px solid #333', marginTop: 20 }} />;
}
