import React, { useState } from 'react';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';

function App() {
  const [zoneW, setZoneW] = useState(10);      // meters
  const [zoneH, setZoneH] = useState(6);       // meters
  const [price4, setPrice4] = useState(20);    // per 4m tile
  const [price2, setPrice2] = useState(12);    // per 2m tile
  const [price1, setPrice1] = useState(6);     // per 1m tile
  const [placements, setPlacements] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const handleOptimize = () => {
    const margin = 0.207; // meters
    const result = optimizeLayout(zoneW, zoneH, margin);
    setPlacements(result.placements);
    setTotalCost(
      result.counts[4] * price4 +
      result.counts[2] * price2 +
      result.counts[1] * price1
    );
  };

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>
      <div className="controls">
        <label>
          Zona lățime (m):
          <input type="number" value={zoneW} onChange={e => setZoneW(parseFloat(e.target.value))} />
        </label>
        <label>
          Zona înălțime (m):
          <input type="number" value={zoneH} onChange={e => setZoneH(parseFloat(e.target.value))} />
        </label>
        <label>
          Preț placă 4m:
          <input type="number" value={price4} onChange={e => setPrice4(parseFloat(e.target.value))} />
        </label>
        <label>
          Preț placă 2m:
          <input type="number" value={price2} onChange={e => setPrice2(parseFloat(e.target.value))} />
        </label>
        <label>
          Preț placă 1m:
          <input type="number" value={price1} onChange={e => setPrice1(parseFloat(e.target.value))} />
        </label>
        <button onClick={handleOptimize}>Optimizează Așezarea</button>
      </div>
      <div className="result">
        <p>Număr plăci 4m: {placements.filter(p => p.size === 4).length}</p>
        <p>Număr plăci 2m: {placements.filter(p => p.size === 2).length}</p>
        <p>Număr plăci 1m: {placements.filter(p => p.size === 1).length}</p>
        <p>Cost total: {totalCost.toFixed(2)} lei</p>
      </div>
      <LayoutCanvas placements={placements} zoneW={zoneW} zoneH={zoneH} margin={0.207} />
    </div>
  );
}

export default App;
