// src/App.js

import React, { useState } from 'react';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';
import './index.css';

export default function App() {
  // Canvas size
  const [zoneW, setZoneW] = useState(10);
  const [zoneH, setZoneH] = useState(6);

  // Polygon definition
  const [polygonPoints, setPolygonPoints] = useState([]);
  const [closed, setClosed] = useState(false);
  const [sideLen, setSideLen] = useState('');
  const [sideAng, setSideAng] = useState('');

  // Layout options
  const [startCorner, setStartCorner] = useState('bottom-left');
  const [orientation, setOrientation] = useState('both');
  const [tileW, setTileW] = useState(1.26);    // short side in m
  const [margin, setMargin] = useState(0.207); // 207 mm in m

  // Tile lengths and pricing
  const lengths = [4, 2, 1];
  const [prices, setPrices] = useState({4:20,2:12,1:6});

  // Placement results
  const [placements, setPlacements] = useState([]);
  const [counts, setCounts] = useState({4:0,2:0,1:0});

  // Add a side by length & angle
  const addSide = () => {
    const L = parseFloat(sideLen);
    const A = parseFloat(sideAng) * Math.PI / 180;
    if (isNaN(L) || isNaN(A)) return;
    const last = polygonPoints.length
      ? polygonPoints[polygonPoints.length - 1]
      : { x: 0, y: 0 };
    setPolygonPoints([
      ...polygonPoints,
      { x: last.x + L * Math.cos(A), y: last.y + L * Math.sin(A) }
    ]);
    setSideLen('');
    setSideAng('');
  };

  // Close polygon
  const finishPoly = () => {
    if (polygonPoints.length >= 3) setClosed(true);
  };

  // Reset everything
  const resetAll = () => {
    setPolygonPoints([]);
    setClosed(false);
    setPlacements([]);
    setCounts({4:0,2:0,1:0});
  };

  // Run the optimizer
  const optimize = () => {
    const { placements: pl, counts: cs } = optimizeLayout(
      polygonPoints,
      lengths,
      tileW,
      margin,
      orientation,
      startCorner
    );
    setPlacements(pl);
    setCounts(cs);
  };

  // Compute tile cost
  const tileCost =
    counts[4] * prices[4] +
    counts[2] * prices[2] +
    counts[1] * prices[1];

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      {/* Canvas size & reset */}
      <div className="controls">
        <label>
          Canvas width (m):
          <input
            type="number"
            value={zoneW}
            onChange={e => setZoneW(+e.target.value || 0)}
          />
        </label>
        <label>
          Canvas height (m):
          <input
            type="number"
            value={zoneH}
            onChange={e => setZoneH(+e.target.value || 0)}
          />
        </label>
        <button onClick={resetAll}>Reset All</button>
      </div>

      {/* Define polygon */}
      <div className="controls">
        <label>
          Side length (m):
          <input
            type="number" step="0.01"
            value={sideLen}
            onChange={e => setSideLen(e.target.value)}
            disabled={closed}
          />
        </label>
        <label>
          Angle (°):
          <input
            type="number" step="1"
            value={sideAng}
            onChange={e => setSideAng(e.target.value)}
            disabled={closed}
          />
        </label>
        <button onClick={addSide} disabled={closed}>Add Side</button>
        <button
          onClick={finishPoly}
          disabled={closed || polygonPoints.length < 3}
        >
          Close Polygon
        </button>
      </div>

      {/* Layout & tile settings */}
      <div className="controls">
        <label>
          Start corner:
          <select
            value={startCorner}
            onChange={e => setStartCorner(e.target.value)}
            disabled={!closed}
          >
            <option value="bottom-left">Bottom‑left</option>
            <option value="bottom-right">Bottom‑right</option>
            <option value="top-right">Top‑right</option>
            <option value="top-left">Top‑left</option>
          </select>
        </label>
        <label>
          Orientation:
          <select
            value={orientation}
            onChange={e => setOrientation(e.target.value)}
            disabled={!closed}
          >
            <option value="both">Both</option>
            <option value="horizontal">Horizontal only</option>
            <option value="vertical">Vertical only</option>
          </select>
        </label>
        <label>
          Tile short side (m):
          <input
            type="number" step="0.01"
            value={tileW}
            onChange={e => setTileW(+e.target.value || 0)}
            disabled={!closed}
          />
        </label>
        <label>
          Margin (m):
          <input
            type="number" step="0.001"
            value={margin}
            onChange={e => setMargin(+e.target.value || 0)}
            disabled={!closed}
          />
        </label>
        <button onClick={optimize} disabled={!closed}>
          Optimize Layout
        </button>
      </div>

      {/* Tile pricing */}
      {placements.length > 0 && (
        <div className="controls">
          <label>
            Price 4 m:
            <input
              type="number" step="0.01"
              value={prices[4]}
              onChange={e => setPrices({...prices,4:+e.target.value||0})}
            />
          </label>
          <label>
            Price 2 m:
            <input
              type="number" step="0.01"
              value={prices[2]}
              onChange={e => setPrices({...prices,2:+e.target.value||0})}
            />
          </label>
          <label>
            Price 1 m:
            <input
              type="number" step="0.01"
              value={prices[1]}
              onChange={e => setPrices({...prices,1:+e.target.value||0})}
            />
          </label>
          <p>Total tile cost: {tileCost.toFixed(2)} lei</p>
        </div>
      )}

      {/* Draggable Canvas */}
      <LayoutCanvas
        zoneW={zoneW}
        zoneH={zoneH}
        polygonPoints={polygonPoints}
        closed={closed}
        placements={placements}
        setPlacements={setPlacements}
        margin={margin}
      />
    </div>
  );
}
