import React, { useState } from 'react';
import LayoutCanvas from './components/LayoutCanvas';
import optimizeLayout from './utils/optimizer';

function App() {
  const [zoneW, setZoneW] = useState(10); // meters
  const [zoneH, setZoneH] = useState(6);  // meters

  const [polygonPoints, setPolygonPoints] = useState([]);       // [{x,y},…]
  const [isPolygonClosed, setIsPolygonClosed] = useState(false);

  const [placements, setPlacements] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  const [price4, setPrice4] = useState(20); // lei per 4 m tile
  const [price2, setPrice2] = useState(12); // lei per 2 m tile
  const [price1, setPrice1] = useState(6);  // lei per 1 m tile

  const handleCanvasClick = (x, y) => {
    if (!isPolygonClosed) {
      setPolygonPoints([...polygonPoints, { x, y }]);
    }
  };

  const handleFinishPolygon = () => {
    if (polygonPoints.length >= 3) {
      setIsPolygonClosed(true);
    }
  };

  const handleClearPolygon = () => {
    setPolygonPoints([]);
    setIsPolygonClosed(false);
    setPlacements([]);
    setTotalCost(0);
  };

  const handleOptimize = () => {
    const margin = 0.207;       // meters
    const tileWidth = 1.26;     // meters
    const lengths = [4, 2, 1];  // meters

    const { placements, counts } = optimizeLayout(
      polygonPoints,
      zoneW,
      zoneH,
      lengths,
      tileWidth,
      margin
    );

    setPlacements(placements);

    const cost =
      counts[4] * price4 +
      counts[2] * price2 +
      counts[1] * price1;
    setTotalCost(cost);
  };

  return (
    <div className="app-container">
      <h1>Tile Layout Optimizer</h1>

      <div className="controls">
        <label>
          Zona lățime (m):
          <input
            type="number"
            value={zoneW}
            onChange={e => setZoneW(parseFloat(e.target.value) || 0)}
          />
        </label>
        <label>
          Zona înălțime (m):
          <input
            type="number"
            value={zoneH}
            onChange={e => setZoneH(parseFloat(e.target.value) || 0)}
          />
        </label>
        <button onClick={handleClearPolygon}>Clear Polygon</button>
      </div>

      <LayoutCanvas
        zoneW={zoneW}
        zoneH={zoneH}
        polygonPoints={polygonPoints}
        isPolygonClosed={isPolygonClosed}
        placements={placements}
        margin={0.207}
        onCanvasClick={handleCanvasClick}
      />

      <div className="controls">
        {!isPolygonClosed && polygonPoints.length >= 3 && (
          <button onClick={handleFinishPolygon}>Finish Polygon</button>
        )}

        {isPolygonClosed && (
          <>
            <label>
              Preț placă 4 m:
              <input
                type="number"
                value={price4}
                onChange={e => setPrice4(parseFloat(e.target.value) || 0)}
              />
            </label>
            <label>
              Preț placă 2 m:
              <input
                type="number"
                value={price2}
                onChange={e => setPrice2(parseFloat(e.target.value) || 0)}
              />
            </label>
            <label>
              Preț placă 1 m:
              <input
                type="number"
                value={price1}
                onChange={e => setPrice1(parseFloat(e.target.value) || 0)}
              />
            </label>
            <button onClick={handleOptimize}>Optimizează Așezarea</button>
          </>
        )}
      </div>

      {isPolygonClosed && (
        <div className="result">
          <p>Număr plăci 4 m: {placements.filter(p => p.length === 4).length}</p>
          <p>Număr plăci 2 m: {placements.filter(p => p.length === 2).length}</p>
          <p>Număr plăci 1 m: {placements.filter(p => p.length === 1).length}</p>
          <p>Cost total: {totalCost.toFixed(2)} lei</p>
        </div>
      )}
    </div>
  );
}

export default App;
